import React, { useState, useEffect } from 'react';

const AuctioneerDashboard = () => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [itemName, setItemName] = useState('');
  const [itemDescription, setItemDescription] = useState('');
  const [itemImages, setItemImages] = useState([]);
  const [startingBid, setStartingBid] = useState('');
  const [category, setCategory] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [items, setItems] = useState([]);

  // Update the current time every second
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const categories = [
    'Motor Vehicles', 'Electronics', 'Land & Real Estate', 'Furniture',
    'Art & Collectibles', 'Jewelry & Watches', 'Antiques', 'Machinery', 'Others'
  ];

  const handleAddItem = (e) => {
    e.preventDefault();
    if (itemImages.length < 1 || itemImages.length > 10) {
      alert('Please upload between 1 and 10 images.');
      return;
    }
    const newItem = {
      name: itemName, description: itemDescription, images: itemImages,
      startingBid, category, startDate, endDate, status: 'pending'
    };
    setItems([...items, newItem]);
    setItemName(''); setItemDescription(''); setItemImages([]);
    setStartingBid(''); setCategory(''); setStartDate(''); setEndDate('');
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 10) {
      alert('You can upload a maximum of 10 images.');
      return;
    }
    setItemImages(files);
  };

  return (
    <div className="container mx-auto p-6 bg-gray-50 min-h-screen">
      <div className="bg-white shadow-lg rounded-lg p-6">
        <h1 className="text-4xl font-bold text-center text-blue-700 mb-4">Auctioneer Dashboard</h1>
        <p className="text-center text-gray-600 text-xl mb-8">Current Time: {currentTime.toLocaleString()}</p>

        <div className="mt-8 bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Add Item to Auction</h2>
          <form onSubmit={handleAddItem} className="space-y-6">
            <div>
              <label htmlFor="itemName" className="block text-lg font-medium text-gray-700">Item Name</label>
              <input
                type="text" id="itemName" value={itemName} onChange={(e) => setItemName(e.target.value)}
                className="mt-2 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                placeholder="Enter item name" required
              />
            </div>

            <div>
              <label htmlFor="itemDescription" className="block text-lg font-medium text-gray-700">Item Description</label>
              <textarea
                id="itemDescription" value={itemDescription} onChange={(e) => setItemDescription(e.target.value)}
                className="mt-2 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                placeholder="Enter item description" required
              />
            </div>

            <div>
              <label htmlFor="itemImages" className="block text-lg font-medium text-gray-700">Item Images (1-10)</label>
              <input
                type="file" id="itemImages" onChange={handleImageChange} multiple accept="image/*"
                className="mt-2 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                required
              />
              <p className="text-sm text-gray-500 mt-2">Upload up to 10 images.</p>
            </div>

            <div>
              <label htmlFor="startingBid" className="block text-lg font-medium text-gray-700">Starting Bid</label>
              <input
                type="number" id="startingBid" value={startingBid} onChange={(e) => setStartingBid(e.target.value)}
                className="mt-2 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                placeholder="Enter starting bid amount" required
              />
            </div>

            <div>
              <label htmlFor="category" className="block text-lg font-medium text-gray-700">Category</label>
              <select
                id="category" value={category} onChange={(e) => setCategory(e.target.value)}
                className="mt-2 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                required
              >
                <option value="">Select Category</option>
                {categories.map((cat) => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
            </div>

            <div>
              <label htmlFor="startDate" className="block text-lg font-medium text-gray-700">Auction Start Date & Time</label>
              <input
                type="datetime-local" id="startDate" value={startDate} onChange={(e) => setStartDate(e.target.value)}
                className="mt-2 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div>
              <label htmlFor="endDate" className="block text-lg font-medium text-gray-700">Auction End Date & Time</label>
              <input
                type="datetime-local" id="endDate" value={endDate} onChange={(e) => setEndDate(e.target.value)}
                className="mt-2 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <button type="submit" className="w-full bg-blue-600 text-white p-3 rounded-lg font-semibold text-lg hover:bg-blue-700 transition">
              Add Item
            </button>
          </form>
        </div>

        {/* Active Auctions */}
        <div className="mt-10 bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-2xl font-semibold text-gray-800 mb-4">Active Auctions</h3>
          {items.length === 0 ? (
            <p className="text-gray-600 text-center">No items added yet.</p>
          ) : (
            <ul className="space-y-6">
              {items.map((item, index) => (
                <li key={index} className="p-6 border border-gray-200 rounded-lg shadow-md">
                  <h4 className="font-bold text-xl text-blue-600">{item.name}</h4>
                  <p className="text-gray-600 mb-4">{item.description}</p>
                  <div className="mt-2 mb-4">
                    <h5 className="text-md font-medium text-gray-700 mb-2">Images:</h5>
                    <div className="flex flex-wrap gap-3">
                      {item.images.map((image, i) => (
                        <img
                          key={i} src={URL.createObjectURL(image)} alt={`Item ${i + 1}`}
                          className="w-24 h-24 object-cover rounded-md border"
                        />
                      ))}
                    </div>
                  </div>
                  <p className="text-gray-800 font-semibold">Starting Bid: ${item.startingBid}</p>
                  <p className="text-gray-600">Category: {item.category}</p>
                  <p className="text-gray-600">Start Date: {new Date(item.startDate).toLocaleString()}</p>
                  <p className="text-gray-600">End Date: {new Date(item.endDate).toLocaleString()}</p>
                  <p className="text-green-600 font-medium">Status: {item.status}</p>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

export default AuctioneerDashboard;
