import React, { useState, useEffect } from "react";
import './index.css';
import Navbar from './components/Navbar';
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Authentication from './components/Authentication';

import HomePage from './components/HomePage';
import AuctioneerDashboard from './pages/AuctioneerDashboard';
import ClientDashboard from './pages/ClientDashboard';
import AdminDashboard from './pages/AdminDashboard';

const App = () => {
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("access_token");

    if (token) {
      try {
        const tokenParts = token.split(".");
        if (tokenParts.length === 3) {
          const decodedToken = JSON.parse(atob(tokenParts[1]));
          setUserRole(decodedToken.role);
        } else {
          console.error("Invalid token format");
          localStorage.removeItem("access_token");
        }
      } catch (error) {
        console.error("Failed to decode token:", error);
        localStorage.removeItem("access_token");
      }
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("access_token");
    setUserRole(null);
  };

  return (
    <Router>
      <div className="flex flex-col items-center justify-center min-w-[1920px] min-h-[1080px] bg-gray-100">
        {userRole && <Navbar userRole={userRole} handleLogout={handleLogout} />}

        <main className="flex-grow flex justify-center items-center w-full">
          <div className="w-full h-full max-w-[1920px] max-h-[1080px] bg-white p-8 rounded-lg shadow-lg">
            <Routes>
              <Route
                path="/"
                element={<HomePage userRole={userRole} handleLogout={handleLogout} />}
              />
              <Route
                path="/login"
                element={
                  userRole ? (
                    <Navigate to={`/${userRole}-dashboard`} />
                  ) : (
                    <Authentication setUserRole={setUserRole} />
                  )
                }
              />
              <Route
                path="/registration"
                element={<Authentication setUserRole={setUserRole} />}
              />
              <Route
                path="/admin-dashboard"
                element={userRole === "admin" ? <AdminDashboard /> : <Navigate to="/" />}
              />
              <Route
                path="/auctioneer-dashboard"
                element={userRole === "auctioneer" ? <AuctioneerDashboard /> : <Navigate to="/" />}
              />
              <Route
                path="/client-dashboard"
                element={userRole === "client" ? <ClientDashboard /> : <Navigate to="/" />}
              />
            </Routes>
          </div>
        </main>
      </div>
    </Router>
  );
};

export default App;
